#!/usr/bin/env python3

from Crypto.Util.number import getPrime, bytes_to_long
from secrets import flag
m = bytes_to_long(flag)

def generate_keys():
    p = getPrime(1024)
    q = getPrime(1024)
    n = p*q
    phi = (p-1)*(q-1)
    while True:
        d = getPrime(256)
        e = pow(d,-1,phi)
        if e.bit_length() == n.bit_length():
            break
    return n,e


n, e = generate_keys()
c = pow(m, e, n)

print(f'n = {n}')
print(f'e = {e}')
print(f'c = {c}')


